const exec = require('child_process').exec;

if (process.platform === "win32") {
    const winInstallScript = exec('validateAndInstallForWindows.bat', function(err, stdout, stderr) {
        if (err) {
            console.log('\x1b[31m', 'Error:');
        }
        console.log(stdout);
    });
    winInstallScript.stdout.on('data', (data) => {
        console.log(data);
    });
} else {
    const installScript = exec('./validateAndInstall.sh', function(err, stdout, stderr) {
        if (err) {
            console.log('\x1b[31m', 'Error:');
        }
        console.log(stdout);
    });
    installScript.stdout.on('data', (data) => {
        console.log(data);
    });
}